def readints():
    return list(map(int, sys.stdin.readline().split()))

def cross(a, b):
    return a[0] * b[1] - a[1] * b[0]

def line_intersection(line1, line2):
    a1, b1, c1 = line1
    a2, b2, c2 = line2
    det = a1 * b2 - a2 * b1
    if det == 0:
        return None
    x = (b1 * c2 - b2 * c1) / det
    y = (a2 * c1 - a1 * c2) / det
    return (x, y)

def is_point_on_line(x, y, a, b, c):
    return abs(a * x + b * y + c) < 1e-8

def bfs(start, end, roads):
    n = len(roads)
    visited = [False] * n
    queue = deque()
    queue.append((start, 0))
    visited[0] = True
    while queue:
        point, steps = queue.popleft()
        for i in range(n):
            if not visited[i]:
                intersection = line_intersection(roads[0], roads[i])
                if intersection and not is_point_on_line(point[0], point[1], roads[i][0], roads[i][1], roads[i][2]):
                    visited[i] = True
                    queue.append((intersection, steps + 1))
                    if intersection == end:
                        return steps + 1
    return -1

