import sys
import math
from collections import deque

def readints():
    return list(map(int, sys.stdin.readline().split()))

def cross(a, b):
    return a[0] * b[1] - a[1] * b[0]

def line_intersection(line1, line2):
    a1, b1, c1 = line1
    a2, b2, c2 = line2
    det = a1 * b2 - a2 * b1
    if det == 0:
        return None
    x = (b1 * c2 - b2 * c1) / det
    y = (a2 * c1 - a1 * c2) / det
    return (x, y)

def point_on_line_side(point, line):
    a, b, c = line
    return a * point[0] + b * point[1] + c

def is_point_between(a, b, p):
    # Check if p lies on the segment between a and b
    def dot(v, w):
        return v[0] * w[0] + v[1] * w[1]
    def cross(v, w):
        return v[0] * w[1] - v[1] * w[0]

    ab = (b[0] - a[0], b[1] - a[1])
    ap = (p[0] - a[0], p[1] - a[1])
    if cross(ab, ap) != 0:
        return False
    if dot(ab, ap) < 0 or dot(ab, ab) < dot(ab, ap):
        return False
    return True

def get_neighbors(point, lines):
    neighbors = []
    for i in range(len(lines)):
        for j in range(i + 1, len(lines)):
            l1 = lines[i]
            l2 = lines[j]
            inter = line_intersection(l1, l2)
            if inter is None:
                continue
            if is_point_between(inter, point, (point[0], point[1])):
                continue
            if not is_point_between(inter, point, (point[0], point[1])):
                continue
            # Check if the point is on the same side of both lines
            d1 = point_on_line_side((point[0], point[1]), l1)
            d2 = point_on_line_side((point[0], point[1]), l2)
            if d1 * d2 > 0:
                continue
            # Now we need to check if the point is on the same side of the other lines
            # We can't do this directly, so we just assume that crossing this intersection
            # moves us to another block
            neighbors.append(inter)
    return neighbors

def bfs(start, end, lines):
    visited = set()
    queue = deque([(start, 0)])
    visited.add(start)
    while queue:
        point, steps = queue.popleft()
        if point == end:
            return steps
        for neighbor in get_neighbors(point, lines):
            if neighbor not in visited:
                visited.add(neighbor)
                queue.append((neighbor, steps + 1))
    return -1

def main():
    x1, y1 = readints()
    x2, y2 = readints()
    n = int(sys.stdin.readline())
    lines = []
    for _ in range(n):
        a, b, c = map(int, sys.stdin.readline().split())
        lines.append((a, b, c))

    home = (x1, y1)
    university = (x2, y2)

    print(bfs(home, university, lines))

if __name__ == "__main__":
    main()

