import sys

def main():
    n = int(sys.stdin.readline())
    strings = [sys.stdin.readline().strip() for _ in range(2 * n - 2)]

    from collections import defaultdict

    # Group strings by their length
    length_to_strings = defaultdict(list)
    for s in strings:
        length_to_strings[len(s)].append(s)

    # Try all possible combinations of prefixes and suffixes for each length
    # We need to assign one string as prefix and one as suffix for each length
    # Then check if there exists a string of length n that is consistent with this assignment

    # Try all possible assignments for each length
    from itertools import product

    # For each length, try both possibilities (first as prefix, second as suffix)
    # and see if a valid string can be formed
    for prefix_suffix_assignments in product([0, 1], repeat=n-1):
        # Build a list of which strings are prefixes and which are suffixes
        prefix_list = []
        suffix_list = []
        for length in range(1, n):
            idx = prefix_suffix_assignments[length - 1]
            if idx == 0:
                prefix_list.append(length_to_strings[length][0])
                suffix_list.append(length_to_strings[length][1])
            else:
                prefix_list.append(length_to_strings[length][1])
                suffix_list.append(length_to_strings[length][0])

        # Try to find a string s of length n that matches all prefixes and suffixes
        found = False
        for candidate in set(prefix_list + suffix_list):
            candidate += candidate[0]  # Ensure it's length n
            if len(candidate) != n:
                continue
            valid = True
            for i in range(1, n):
                if candidate[:i] != prefix_list[i - 1]:
                    valid = False
                    break
                if candidate[-i:] != suffix_list[i - 1]:
                    valid = False
                    break
            if valid:
                found = True
                break

        if found:
            # Construct the answer string
            result = []
            for i in range(2 * n - 2):
                if i < n - 1:
                    if prefix_suffix_assignments[i] == 0:
                        result.append('P')
                    else:
                        result.append('S')
                else:
                    if prefix_suffix_assignments[i - (n - 1)] == 0:
                        result.append('P')
                    else:
                        result.append('S')
            print(''.join(result))
            return

