        edges.append((a, b))
        graph[a].append(b)
        graph[b].append(a)

    m = n - k
    keep_candidates = list(range(n, n - m, -1))
    if len(keep_candidates) < m:
        keep_candidates = list(range(1, n+1))
        keep_candidates.sort(reverse=True)
        keep_candidates = keep_candidates[:m]

    removed_set = set(range(1, n+1)) - set(keep_candidates)
    if len(removed_set) != k:
        print(" ".join(map(str, sorted(removed_set)))))
        return

