        visited = {start_node}
        parent = {}
        while q:
            node = q.popleft()
            for neighbor in graph[node]:
                if neighbor not in visited:
                    visited.add(neighbor)
                    parent[neighbor] = node
                    q.append(neighbor)

        if len(visited) != m:
            raise Exception("Not all kept nodes are connected")
    except Exception as e:
