        edges.append((a, b))

    sorted_nodes = sorted(range(1, n+1), reverse=True)

    parent = list(range(n+1))
    rank = [0] * (n+1)

    def find(x):
        if parent[x] != x:
            parent[x] = find(parent[x])
        return parent[x]

    def union(x, y):
        rx = find(x)
        ry = find(y)
        if rx == ry:
            return False
        if rank[rx] < rank[ry]:
            parent[rx] = ry
        elif rank[rx] > rank[ry]:
            parent[ry] = rx
        else:
            parent[ry] = rx
            rank[rx] += 1
        return True

    graph = [[] for _ in range(n+1)]
    for a, b in edges:
        graph[a].append(b)
        graph[b].append(a)

    m = n - k
