    while current_set_size < m and pointer < n:
        node = sorted_nodes[pointer]
        selected_nodes.add(node)
        current_set_size += 1

        for neighbor in graph[node]:
            if neighbor in selected_nodes:
                continue
            if find(node) != find(neighbor):
                union(node, neighbor)

