    n = int(data[0])
    k = int(data[1])
    edges = []
    index = 2
    graph = defaultdict(list)

    for _ in range(n-1):
        a = int(data[index])
        b = int(data[index+1])
        index += 2
        graph[a].append(b)
        graph[b].append(a)

    order = list(range(1, n+1))
    order.sort(reverse=True)

    kept_count = n - k
    T = order[:kept_count]

    parent = [-1] * (n+1)
    depth = [-1] * (n+1)
    children = defaultdict(list)
    stack = [1]
    depth[1] = 0
    parent[1] = 0

    while stack:
        node = stack.pop()
        for neighbor in graph[node]:
            if neighbor == parent[node]:
                continue
            parent[neighbor] = node
            depth[neighbor] = depth[node] + 1
            children[node].append(neighbor)
            stack.append(neighbor)

    def lca(a, b):
        if depth[a] < depth[b]:
            a, b = b, a
        while depth[a] > depth[b]:
            a = parent[a]
        while a != b:
            a = parent[a]
            b = parent[b]
        return a

    removed_set = set(order[kept_count:])
    components = {}
    comp_id = 0
    for node in T:
        root = node
        path_nodes = []
        current = node
        while current != 0:
            path_nodes.append(current)
            current = parent[current]
        components[node] = set(path_nodes)
        comp_id += 1

    comp_representative = {}
    for node in T:
        rep = node
        for other in T:
            if node == other:
                continue
            if lca(node, other) in components[other]:
                rep = lca(node, other)
                break
        components[node] = components.get(rep, components[node])
        comp_representative[node] = rep

    comp_to_remove = []
    for comp in components.values():
        min_node = min(comp)
        if min_node not in T:
            comp_to_remove.append(min_node)

    if len(comp_to_remove) <= k:
        removed_list = sorted(comp_to_remove[:k])
        print(" ".join(map(str, removed_list)))
    else:
        candidate_removals = set()
