        if len(candidate_removals) >= k:
            print(" ".join(map(str, candidate_removals[:k])))
        else:
            needed_from_removed = k - len(candidate_removals)
            removed_candidates = sorted(removed_set)[:needed_from_removed]
            final_removed = candidate_removals + removed_candidates
