        return num * pow(den, mod - 2, mod) % mod

    def dp(level):
        if level == 0:
            return 1
        res = 0
        for x in range(0, k + 1):
            if x < f_val:
                continue
            ways_here = 0
            for y in range(0, k + 1 - x):
                if y < f_val:
                    continue
                ways_here = (ways_here + 1) % mod
            res = (res + nCr(k + 1, x + 1) * dp(level - 1) % mod) % mod
        return res

    total_ways = 0
    for x in range(0, k + 1):
        if x < f_val:
            continue
        for y in range(0, k + 1 - x):
            if y < f_val:
                continue
            total_ways = (total_ways + 1) % mod

    total_ways = total_ways * pow(pow(2, total_levels * (total_levels - 1) // 2, mod), total_leaves, mod) % mod

    ans = 0
    for x in range(f_val, k + 1):
        for y in range(f_val, k + 1 - x):
            ans = (ans + 1) % mod

    ans = ans * pow(2, total_leaves * (total_levels - 1), mod) % mod

    print(ans)

