        res = 0
        for bit in '01':
            child = s + bit
            res += dfs(child)
        return min(total_leaves, res)

    dp = {}

    def memoized_dfs(s):
        if s in dp:
            return dp[s]
        if len(s) == n:
            return 1
        res = 0
        for bit in '01':
            child = s + bit
            res += memoized_dfs(child)
        res = min(total_leaves, res)
        dp[s] = res
        return res

    memoized_dfs("")
    root_val = dp[""]

    if root_val < f:
        print(0)
        return

    def count_assignments(s, target):
