def main():
    import sys
    data = sys.stdin.read().split()
    n = int(data[0])
    k_val = int(data[1])
    f_val = int(data[2])

    total_nodes = (1 << (n + 1)) - 1
    dp = [[0] * (k_val + 1) for _ in range(total_nodes)]

    dp[0][0] = 1

    for i in range(total_nodes):
        if dp[i][0] == 0:
            continue
        left_child = 2 * i + 1
        right_child = 2 * i + 2

        if left_child >= total_nodes:
            continue

        for c in range(k_val + 1):
            if dp[i][c] == 0:
                continue

            # For left child
            for new_c in range(c + 1):
                if new_c <= k_val:
                    dp[left_child][new_c] = (dp[left_child][new_c] + dp[i][c]) % MOD

            # For right child
            for new_c in range(c + 1):
                if new_c <= k_val:
                    dp[right_child][new_c] = (dp[right_child][new_c] + dp[i][c]) % MOD

    ans = 0
    for i in range(total_nodes):
        for c_val in range(f_val + 1):
            if c_val <= k_val:
                ans = (ans + dp[i][c_val]) % MOD

    print(ans)

