        nums = sorted([int(tiles[i]) for i in indices])
        if len(nums) == 3 and nums[2] == nums[0] + 2:
            print(0)
            return

    # Check for one-draw possibilities
    from collections import defaultdict
    count_tiles = defaultdict(int)
    for t in tiles:
        count_tiles[t] += 1

    count_suits = defaultdict(list)
    for i in range(3):
        count_suits[suits[i]].append(tiles[i])

    # Check for two same tiles that can be completed by one draw for triplet
    found_one = False
    for suit in ['m', 'p', 's']:
        if len(count_suits[suit]) >= 2:
            nums = sorted(set(count_suits[suit]))
            num_count = {}
            for num_str in nums:
                num_count[num_str] = count_suits[suit].count(num_str)
            for num_str in nums:
                if num_count[num_str] == 2:
                    # Drawing one more of this tile would complete a triplet
                    found_one = True
                    break
            if found_one:
                break

    if found_one:
        print(1)
        return

    # Check for one-draw possibilities for sequences
    for suit in ['m', 'p', 's']:
        if len(count_suits[suit]) < 2:
            continue
        nums = sorted(set(count_suits[suit]))
        num_count = {}
        for num_str in nums:
            num_count[num_str] = count_suits[suit].count(num_str)
        num_list = [int(x) for x in nums]
        for i in range(len(num_list) - 1):
            if num_list[i+1] - num_list[i] == 1:
                if num_count[str(num_list[i])] >= 1 and num_count[str(num_list[i+1])] >= 1:
                    # We have two consecutive numbers, now check if we can form a sequence by drawing one tile
                    # The sequence could be [num_list[i]-1, num_list[i], num_list[i+1]] or [num_list[i], num_list[i+1], num_list[i+1]+1]
                    if num_list[i] - 1 in num_count:
                        if num_count.get(str(num_list[i] - 1), 0) >= 1:
