                                break
            elif num_list[i+1] - num_list[i] == 2:
                mid = num_list[i] + 1
                if str(mid) in num_count and num_count.get(str(mid), 0) >= 1:
                    if num_count[num_list[i]] >= 1 and num_count[num_list[i+1]] >= 1:
                        found_one = True
