    data = input().split()
    tiles = [tile[:2] for tile in data]

    # Check for existing mentsu: either triplet or sequence
    def is_triplet(tiles):
        from collections import Counter
        count = Counter(tiles)
        for tile, cnt in count.items():
            if cnt >= 3:
                return True
        return False

    def is_sequence(tiles):
        if len(set(tiles)) < 3:
            return False
        tiles_sorted = sorted(set(tiles), key=lambda x: (x[0], 'm','p','s'))
        if len(tiles_sorted) != 3:
            return False
        num1, suit1 = tiles_sorted[0]
        num2, suit2 = tiles_sorted[1]
        num3, suit3 = tiles_sorted[2]
        if suit1 == suit2 == suit3 and int(num1)+1 == int(num2) and int(num2)+1 == int(num3):
            return True
        return False

    if is_triplet(tiles) or is_sequence(tiles):
        print(0)
        return

    # Count duplicates for triplet
    from collections import Counter
    count_tiles = Counter(tiles)
    max_count = max(count_tiles.values())
    if max_count == 2:
        print(1)
        return

    # Check for one-draw sequence completion
    suits = set(tile[1] for tile in tiles)
    if len(suits) == 1:
        nums = sorted(set(int(tile[0]) for tile in tiles))
