        if n == 2:
            min_val = min(nums)
            max_val = max(nums)
            if max_val - min_val == 1:
                print(1)
                return
        elif n == 3:
            min_val = min(nums)
            max_val = max(nums)
            if max_val - min_val == 2:
                print(1)
                return

    # Check for two-draw sequence completion
    if len(suits) == 1:
        nums = sorted(set(int(tile[0]) for tile in tiles))
        n = len(nums)
        if n == 1:
            print(2)
            return
        elif n == 2:
            min_val = min(nums)
            max_val = max(nums)
            if max_val - min_val == 1:
                print(2)
