def main():
    data = input().split()
    tiles = [tile for tile in data if tile]

    n = len(tiles)
    if n != 3:
        print(0)
        return

    # Check for existing mentsu (koutsu or shuntsu)
    from collections import defaultdict
    freq = defaultdict(int)
    suits = defaultdict(list)
    for tile in tiles:
        num = int(tile[0])
        s = tile[1]
        freq[tile] += 1
        suits[s].append(num)

    # Check koutsu (triplet)
    if any(count >= 3 for count in freq.values()):
        print(0)
        return

    # Check shuntsu (sequence)
    for s in suits:
        nums = sorted(set(suits[s]))
        if len(nums) < 3:
            continue
        for i in range(len(nums) - 2):
            if nums[i+1] == nums[i] + 1 and nums[i+2] == nums[i] + 2:
                print(0)
                return

    # Now, consider drawing 1, 2, or 3 tiles

    # Case 1: Drawing 1 tile
    # Possibility 1: Complete a triplet by drawing one more tile of a duplicated tile.
    if any(count == 2 for count in freq.values()):
        print(1)
        return

    # Possibility 2: Complete a sequence by drawing one tile.
    for s, nums_list in suits.items():
        nums_set = set(nums_list)
        if len(nums_set) < 2:
            continue
        for num in range(1, 10):
            if num - 1 not in nums_set and num - 2 not in nums_set:
                continue
            if (num - 1 in nums_set and num - 2 in nums_set):
                print(1)
                return

    # Case 2: Drawing 2 tiles
    # Possibility 1: Complete a triplet by drawing two more tiles of a duplicated tile.
    if any(count == 2 for count in freq.values()):
        print(2)
        return

    # Possibility 2: Complete a sequence by drawing two tiles.
    for s, nums_list in suits.items():
        nums_set = set(nums_list)
        if len(nums_set) < 2:
            continue
        for num in range(1, 10):
            if (num - 1 in nums_set and num - 2 not in nums_set) or (num - 1 not in nums_set and num - 2 in nums_set):
                print(2)
                return

    # Case 3: Drawing 3 tiles
    # Possibility: Complete a triplet by drawing three tiles of a single tile (if none duplicated).
    if all(count <= 1 for count in freq.values()):
        print(3)
    else:
        print(2)

