def can_form_with_one_draw(tiles):
    # Case 1: Triplet with one draw
    from collections import defaultdict
    count_suit = defaultdict(int)
    count_num = defaultdict(int)
    for tile in tiles:
        count_suit[tile[1]] += 1
        count_num[tile[0]] += 1

    for suit, cnt in count_suit.items():
        if cnt >= 2:
            return True

    # Case 2: Shuntsu with one draw
    suits = set([t[1] for t in tiles])
    for suit in suits:
        num_tiles = [int(tile[0]) for tile in tiles if tile[1] == suit]
        num_tiles.sort()
        seen = set(num_tiles)
        for num in num_tiles:
            if num + 1 in seen and num + 2 in seen:
                return True

    return False

def can_form_with_two_draws(tiles):
    from collections import defaultdict
    count_suit = defaultdict(int)
    count_num = defaultdict(int)
    for tile in tiles:
        count_suit[tile[1]] += 1
        count_num[tile[0]] += 1

    # Case 1: Form a triplet with two draws
    for tile in tiles:
        d, s = tile[0], tile[1]
        count_needed = 3 - count_num[d]
        if count_needed <= 2 and count_needed > 0:
            return True

    # Case 2: Form a shuntsu with two draws
    suits = set([t[1] for t in tiles])
    for suit in suits:
        num_tiles = [int(tile[0]) for tile in tiles if tile[1] == suit]
        num_tiles.sort()
        seen_nums = set(num_tiles)
        for num in num_tiles:
            if num + 1 in seen_nums and num + 2 in seen_nums:
                return True
            if num + 1 in seen_nums and num + 2 not in seen_nums:
                continue
            if num + 2 in seen_nums and num + 1 not in seen_nums:
                continue

        # Consider having two tiles of the same suit but not forming a sequence
        if len(num_tiles) < 3:
            if len(num_tiles) == 2:
                for candidate in range(min(num_tiles) - 1, max(num_tiles) + 3):
                    if candidate not in seen_nums:
                        if candidate + 1 not in seen_nums:
                            if candidate + 2 not in seen_nums:
                                continue
                        else:
                            return True
            elif len(num_tiles) == 1:
                for candidate in range(num_tiles[0] - 2, num_tiles[0] + 3):
                    if candidate == num_tiles[0]:
                        continue
                    if candidate + 1 in seen_nums and candidate + 2 in seen_nums:
                        return True

    return False

