def main():
    import sys
    data = sys.stdin.read().splitlines()
    n = int(data[0].strip())
    A = data[1].strip()

    total_digits = 2 * n

    memo = {}

    def dp(pos, count_h, count_m, num_h, num_m):
        if pos == total_digits:
            return 0

        key = (pos, count_h, count_m, num_h, num_m)
        if key in memo:
            return memo[key]

        digit_char = A[pos]
        d = int(digit_char)

        option1 = None
        if count_h < n:
            new_num_h = num_h * 10 + d
            rest1 = dp(pos + 1, count_h + 1, count_m, new_num_h, num_m)
            option1 = rest1

        option2 = None
        if count_m < n:
            new_num_m = num_m * 10 + d
            rest2 = dp(pos + 1, count_h, count_m + 1, num_h, new_num_m)
            option2 = rest2

        best = -1
        if option1 is not None:
            best = option1
        if option2 is not None:
            if best == -1:
                best = option2
            else:
                best = max(best, option2)

        memo[key] = best
        return best

    def backtrack(pos, count_h, count_m, num_h, num_m):
        if pos == total_digits:
            return ""

        digit_char = A[pos]
        d = int(digit_char)

        option1 = None
        if count_h < n:
            new_num_h = num_h * 10 + d
            rest = backtrack(pos + 1, count_h + 1, count_m, new_num_h, num_m)
            if rest is None:
                return "H" + rest
            else:
                return "H" + rest

        option2 = None
        if count_m < n:
            new_num_m = num_m * 10 + d
            rest = backtrack(pos + 1, count_h, count_m + 1, num_h, new_num_m)
            if rest is None:
                return "M" + rest
            else:
                return "M" + rest

        return None

    total_sum = dp(0, 0, 0, 0, 0)
    result_sequence = backtrack(0, 0, 0, 0, 0)
    print(result_sequence)

