            arr.append(i)
            i += 1
        else:
            break

    while len(arr) > 0 and total_triangular < p:
        last = arr.pop()
        remaining_after_remove = p - total_triangular
        t_last = last*(last-1)//2
        new_remaining = remaining_after_remove - t_last
        if new_remaining == 0:
            pass
        elif new_remaining > 0:
            # Find the largest k such that T(k) <= new_remaining
            k = 1
            while k*(k-1)//2 <= new_remaining:
                k += 1
                k -= 1
            total_triangular += t_last + k*(k-1)//2
            arr.append(last)
            arr.append(k)
            arr.sort(reverse=True)
            break
        else:
