        total_nodes += 1

    while total_nodes < n:
        arr.append(1)
        total_nodes += 1

    while total_nodes > n:
        if arr[-1] > 1:
            x = arr.pop()
            arr.append(x-1)
            arr.append(1)
            total_nodes = sum(arr)
        else:
            break

    arr.sort(reverse=True)
    s = 0
    for size in arr:
        s += size
        if s > n:
            break

    m = n*(n-1)//2
    for size in arr:
        m -= size*(size-1)//2

    unidirectional = 0
    for i in range(len(arr)):
        for j in range(i+1, len(arr)):
            if arr[i] == arr[j]:
                continue
            else:
                if arr[i] > arr[j]:
                    unidirectional += arr[i]*arr[j]
                else:
                    unidirectional += arr[i]*arr[j]

    unidirectional //= 2
    print(f"{n} {unidirectional}")

