def main():
    data = sys.stdin.readline().split()
    n = int(data[0])
    mod = int(data[1])

    if n == 0:
        print(0)
        return

    max_inv = n * (n-1) // 2

    fact = [1] * (n+1)
    inv_fact = [1] * (n+1)
    for i in range(1, n+1):
        fact[i] = fact[i-1] * i % mod

    inv_fact[n] = pow(fact[n], -1, mod) if mod != 1 else 1
    for i in range(n-1, -1, -1):
        inv_fact[i] = inv_fact[i+1] * (i+1) % mod

    def nCr(n_val, r):
        if r < 0 or r > n_val:
            return 0
        return fact[n_val] * inv_fact[r] % mod * inv_fact[n_val - r] % mod

    def get_inversion_permutations_count(k):
        total = 0
        for j in range(1, n+1):
            if k < 0:
                break
            total += nCr(n, j) * get_inversion_permutations_count(j, k - j*(j-1)//2)
        return total % mod

    def get_inversion_permutations_count(start, remaining):
        if start == n:
            return 1 if remaining == 0 else 0
        total = 0
        for i in range(1, n - start + 1):
            if i > remaining:
                break
            total += get_inversion_permutations_count(start+1, remaining - i)
        return total

    inv_counts = [0] * (max_inv+1)
    for k in range(0, max_inv+1):
        inv_counts[k] = get_inversion_permutations_count(0, k)

    total_permutations = fact[n]
    total_pairs = 0

    for i in range(0, n):
        for j in range(i+1, n+1):
            if i >= j:
                continue
            total_pairs = (total_pairs + nCr(n, i) * nCr(n-i, j)) % mod

    total_pairs = total_pairs % mod

    ans = 0
    for k in range(0, max_inv+1):
        for l in range(0, k):
            ans = (ans + inv_counts[k] * inv_counts[l]) % mod

    print(ans % mod)

