        a_i = a[i]
        prev_sum = prefix_sums[i]
        for p in range(0, 101):
            if p == 100:
                if a_i == 0:
                    continue
                L1 = (100 * a_i + 99) // 100
                R1 = a_i
                if L1 <= R1:
                    num1 = 100 * total_sum - 100 * prev_sum
                    if num1 <= 0:
                        x_min2 = 0
                    else:
                        x_min2 = (num1 + 99) // 100
                    x_max2 = (num1 + 99) // 100
                    x_high_condition2 = ((100 * total_sum + 1) // 100) - 1
                    if x_min2 <= x_high_condition2:
                        x_low = max(L1, x_min2)
                        x_high = min(R1, x_high_condition2)
                        if x_low <= x_high:
                            result_set.add(p)
                    else:
                        continue
                else:
                    continue
            else:
                L1 = (p * a_i + 99) // 100
                R1_temp = (p+1) * a_i // 100
                if (p+1) * a_i % 100 == 0:
                    R1 = R1_temp - 1
                else:
                    R1 = R1_temp
                if L1 > R1 or L1 > a_i:
