import sys

def max_games(n):
    # The maximum number of games the winner can play is the largest k such that
    # the (k+1)-th Fibonacci number is <= n
    # Because each game requires one player to be eliminated, and in a knockout tournament,
    # the number of games is n - 1, but here we are maximizing the number of games the winner plays.
    # This problem is equivalent to finding the largest k such that Fib(k+2) <= n
    # We use a fast doubling method to compute Fibonacci numbers efficiently for large n

    def fib(n):
        # Fast doubling method to compute Fibonacci numbers
        def fib_pair(n):
            if n == 0:
                return (0, 1)
            a, b = fib_pair(n >> 1)
            c = a * (2 * b - a)
            d = a*a + b*b
            if n & 1:
                return (d, c + d)
            else:
                return (c, d)
        return fib_pair(n)[0]

    # Find the largest k such that Fib(k+2) <= n
    k = 0
    while True:
        f = fib(k + 2)
        if f > n:
            break
        k += 1
    return k

