    if a == b:
        return 0

    d = abs(a - b)
    min_lcm = float('inf')
    best_k = 0

    # Try all divisors of d up to sqrt(d)
    for i in range(1, int(math.isqrt(d)) + 1):
        if d % i == 0:
            # Check i
            k = (a - b) + i
            if k >= 0:
                current_lcm = lcm(a + k, b + k)
                if current_lcm < min_lcm or (current_lcm == min_lcm and k < best_k):
                    min_lcm = current_lcm
                    best_k = k
            # Check d // i
            j = d // i
            k = (a - b) + j
            if k >= 0:
                current_lcm = lcm(a + k, b + k)
                if current_lcm < min_lcm or (current_lcm == min_lcm and k < best_k):
                    min_lcm = current_lcm
                    best_k = k

