def main():
    data = sys.stdin.read().split()
    if not data:
        print("NO")
        return

    n = int(data[0])
    m = int(data[1])
    edges = []
    index = 2
    for _ in range(m):
        u = int(data[index])
        v = int(data[index+1])
        g = int(data[index+2])
        l = int(data[index+3])
        index += 4
        edges.append((u-1, v-1, g, l))

    graph = [[] for _ in range(n)]
    for u, v, g, l in edges:
        if l % g != 0:
            print("NO")
            return

        graph[u].append((v, g, l))
        graph[v].append((u, g, l))

    base_vals = [0] * n
    T_vals = [0] * n

    for i in range(n):
        if graph[i]:
            _, g, l = graph[i][0]
            T = l // g
            base_vals[i] = g
            T_vals[i] = T
        else:
            base_vals[i] = 0
            T_vals[i] = 0

    visited = [False] * n
    q = deque()
    for i in range(n):
        if not visited[i] and graph[i]:
            q.append(i)
            visited[i] = True

    parent = [-1] * n
    while q:
        node = q.popleft()
        for neighbor, g, l in graph[node]:
            if not visited[neighbor]:
                visited[neighbor] = True
                parent[neighbor] = node
                q.append(neighbor)

    result = [0] * n
    for i in range(n):
        if i == 0:
            result[i] = 1
            continue

        if parent[i] == -1:
            continue

        p = parent[i]
        if base_vals[p] == 0:
            continue

        g_p, l_p = None, None
        for nb, g_val, l_val in graph[p]:
            if nb == i:
                g_p = g_val
                l_p = l_val
                break

        T_p = l_p // g_p

        if base_vals[i] == 0:
            base_vals[i] = base_vals[p]
            T_vals[i] = T_p

        if base_vals[i] != base_vals[p]:
            print("NO")
            return

        if T_vals[i] == 0:
            T_vals[i] = T_p

        if result[p] == 0:
            g_p = base_vals[p]
            x = result[p] // g_p
            y = result[i] // base_vals[i]
            if x * y != T_p or gcd(x, y) != 1:
                print("NO")
                return

    for i in range(n):
        if T_vals[i] != 0:
            x = result[i] // base_vals[i]
            if x * x != T_vals[i]:
                print("NO")
                return

    print("YES")
    for i in range(n):
        if base_vals[i] == 0:
            result[i] = 1
        else:
            if T_vals[i] == 0:
                x = 1
            else:
                x = 1
                for j in range(2, int(T_vals[i]**0.5)+1):
                    cnt = 0
                    while T_vals[i] % j == 0:
                        cnt += 1
                        T_vals[i] //= j
                    if cnt > 0:
                        x *= j
                if T_vals[i] > 1:
                    x *= T_vals[i]

            result[i] = base_vals[i] * x

    print(" ".join(map(str, result)))

def gcd(a, b):
    if b == 0:
        return a
    return gcd(b, a % b)

