from collections import defaultdict

def main():
    data = sys.stdin.read().strip().split()
    if not data:
        print("NO")
        return

    it = iter(data)
    n = int(next(it)); m = int(next(it))

    graph = defaultdict(list)
    for _ in range(m):
        u = int(next(it)); v = int(next(it))
        g_val = int(next(it)); l_val = int(next(it))
        graph[u].append((v, g_val, l_val))
        graph[v].append((u, g_val, l_val))

    if m == 0:
        print("YES")
        print(" ".join(["1"] * n))
        return

    ans = [0] * (n+1)
    stack = []
    for i in range(1, n+1):
        stack.append(i)
        break

    visited = [False] * (n+1)
    parent = {}
    for i in range(1, n+1):
        visited[i] = False

    def dfs(node, base):
        visited[node] = True
        ans[node] = base
        for neighbor, g_val, l_val in graph[node]:
            if neighbor == parent.get(node, None):
                continue
            if visited[neighbor]:
                if ans[neighbor] != (g_val * l_val) // base:
                    print("NO")
                    return
                continue
            stack.append(neighbor)
            parent[neighbor] = node
            dfs(neighbor, (g_val * l_val) // base)

    try:
        dfs(1, 1)
    except RecursionError:
        print("NO")
        return

    for i in range(1, n+1):
        if not visited[i]:
            stack.append(i)
            parent[i] = None
            dfs(i, 1)

    for i in range(1, n+1):
        if not visited[i]:
            print("NO")
            return

    for u in range(1, n+1):
        for v, g_val, l_val in graph[u]:
            if gcd(ans[u], ans[v]) != g_val or (ans[u] * ans[v]) // gcd(ans[u], ans[v]) != l_val:
                print("NO")
                return

    print("YES")
    print(" ".join(map(str, ans[1:n+1])))

