    def inv_fact(m):
        return pow(fact(m), MOD-2, MOD)

    def nCr(n, r):
        if r < 0 or r > n:
            return 0
        return fact(n) * inv_fact(r) % MOD * inv_fact(n-r) % MOD

    def poly_mult(a, b):
        deg = len(a) + len(b) - 1
        c = [0] * deg
        for i in range(len(a)):
