def main():
    import sys
    input = sys.stdin.read().split()
    n = int(input[0])

    if n % 2 != 0:
        print(0)
        return

    max_n = 500000
    fact = [1] * (max_n + 1)
    inv_fact = [1] * (max_n + 1)

    for i in range(1, max_n + 1):
        fact[i] = fact[i - 1] * i % MOD

    inv_fact[max_n] = pow(fact[max_n], MOD - 2, MOD)
    for i in range(max_n, 0, -1):
        inv_fact[i - 1] = inv_fact[i] * i % MOD

    def nCr(n_val, r):
        if r < 0 or r > n_val:
            return 0
        return fact[n_val] * inv_fact[r] % MOD * inv_fact[n_val - r] % MOD

    def combination_contribution(a, b, total):
        ways = nCr(total, a) * nCr(total - a, b) % MOD
        return ways

    def solve_for_edge_length(L):
        if L == 0:
            return 0
        if n == 2:
            return 1 if L == 1 else 0

        total_ways = pow(n, n - 2, MOD)
        ways_with_edge = pow(n, n - 3, MOD)
        count_trees_with_edge_on_path = (ways_with_edge * 2) % MOD

        half = n // 2
        if L <= half:
            k = half - L + 1
        else:
            k = L - half + 1

        total_good_edges = 0
        for i in range(1, min(k, n - 1) + 1):
            left_size = i
            right_size = n - i
            ways_to_choose_left = nCr(n - 2, left_size - 1)
            ways_to_choose_right = nCr(n - 2, right_size - 1)
            ways_to_form_subtrees = (pow(left_size, left_size - 2, MOD) if left_size >= 2 else 1) * (pow(right_size, right_size - 2, MOD) if right_size >= 2 else 1) % MOD
            total_subtree_ways = ways_to_choose_left * ways_to_choose_right % MOD * ways_to_form_subtrees % MOD

            if L == 1:
                total_good_edges += total_subtree_ways * (ways_with_edge * 2) % MOD
            elif L == 2:
                total_good_edges += total_subtree_ways * (ways_with_edge * 2) % MOD * 2
            else:
                total_good_edges += total_subtree_ways * (ways_with_edge * 2) % MOD * ((L + 1) // 2)

        total_good_edges %= MOD
        return total_good_edges

    edge_contributions = []
    for length in range(1, n):
        contrib = solve_for_edge_length(length)
        edge_contributions.append(contrib)

    total_sum = 0
    for i in range(len(edge_contributions)):
        if i < len(edge_contributions):
            total_sum = (total_sum + edge_contributions[i]) % MOD

    print(total_sum)

