def mod_inv(a, mod=MOD):
    # Using Fermat's little theorem for modular inverse
    return pow(a, mod-2, mod)

def main():
    import sys
    input = sys.stdin.read().split()
    n = int(input[0])

    if n % 2 != 0:
        print(0)
        return

    half = n // 2
    fact = [1] * (half + 1)
    inv_fact = [1] * (half + 1)

    for i in range(1, half + 1):
        fact[i] = fact[i-1] * i % MOD

    inv_fact[half] = mod_inv(fact[half])
    for i in range(half, 0, -1):
        inv_fact[i-1] = inv_fact[i] * i % MOD

    binom = lambda k: fact[k] * inv_fact[k] % MOD * inv_fact[half - k] % MOD if k <= half else 0

    total = 0
    for k in range(0, half + 1):
        term = binom(k) * pow(2, half - k, MOD) % MOD
        if (k % 2 == 0):
            total = (total + term) % MOD
        else:
            total = (total - term) % MOD

    total = total * pow(2, n - 2, MOD) % MOD
    total = total * pow(2, n - 1, MOD) % MOD
    total = total * pow(2, half, MOD) % MOD

    print(total % MOD)

