def min_ternary_string(s):
    # Convert string to list for easier manipulation
    chars = list(s)
    n = len(chars)

    # Count positions of '0', '1', '2'
    zero_pos = []
    one_pos = []
    two_pos = []

    for i, c in enumerate(chars):
        if c == '0':
            zero_pos.append(i)
        elif c == '1':
            one_pos.append(i)
        else:
            two_pos.append(i)

    # Result will be built by placing '0's, then '1's, then '2's
    result = [''] * n

    # Place '0's
    for i in range(len(zero_pos)):
        result[zero_pos[i]] = '0'

    # Place '1's
    idx = 0
    for i in range(len(one_pos)):
        while idx < len(zero_pos) and result[zero_pos[idx]] == '0':
            idx += 1
        result[one_pos[i]] = '1'

    # Place '2's
    idx = 0
    for i in range(len(two_pos)):
        while idx < len(zero_pos) and result[zero_pos[idx]] == '0':
            idx += 1
        while idx < len(one_pos) and result[one_pos[idx]] == '1':
            idx += 1
        result[two_pos[i]] = '2'

    return ''.join(result)

