    B = int(data[1])
    rebels = []
    index = 2
    for i in range(R):
        x = int(data[index]); y = int(data[index+1]); index += 2
        rebels.append((x, y))

    bases = []
    for i in range(B):
        x = int(data[index]); y = int(data[index+1]); index += 2
        bases.append((x, y))

    if R != B:
        print("No")
        return

    n = R
    bases_sorted = sorted(bases, key=lambda p: (p[0], p[1]))
    rebels_sorted = sorted(rebels, key=lambda p: (p[0], p[1]))

    def orientation(a, b, c):
        ax, ay = a
        bx, by = b
        cx, cy = c
        return (bx - ax) * (cy - ay) - (by - ay) * (cx - ax)

    def segments_intersect(a, b, c, d):
        o1 = orientation(a, b, c)
        o2 = orientation(a, b, d)
        o3 = orientation(c, d, a)
        o4 = orientation(c, d, b)
        return (o1 * o2 < 0) and (o3 * o4 < 0)

    used_rebel = [False] * n
    assign_base = [-1] * n

    def backtrack(index):
        if index == n:
            return True

        x0, y0 = bases_sorted[index]
        for j in range(n):
            if not used_rebel[j]:
                x1, y1 = rebels_sorted[j]
                used_rebel[j] = True
                valid = True
                for k in range(index):
                    x2, y2 = bases_sorted[k]
                    x3, y3 = rebels_sorted[assign_base[k]]
                    if segments_intersect((x0, y0), (x1, y1), (x2, y2), (x3, y3)):
                        valid = False
                        break
                if valid:
                    assign_base[index] = j
                    if backtrack(index + 1):
                        return True
                used_rebel[j] = False
        return False

    if backtrack(0):
        print("Yes")
    else:
        print("No")

