        count = 0
        j = n - 1
        for i in range(n-1, -1, -1):
            while j >= 0 and a[i] + b[j] > tot:
                count += 1
                j -= 1
            if j < 0:
                break
        return count

    def best_place():
        low, high = 0, 400000
        ans = 400000
        while low <= high:
            mid = (low + high) // 2
            if count_greater_or_equal(mid) >= n:
                ans = mid
                high = mid - 1
            else:
                low = mid + 1
        return ans

    def worst_place():
        low, high = 0, 400000
        ans = 400000
        while low <= high:
            mid = (low + high) // 2
            if count_greater_or_equal(mid) < n:
                ans = mid
                low = mid + 1
            else:
                high = mid - 1
        return ans

    best_score = best_place()
    worst_score = worst_place()

    def find_rank(score):
