from collections import deque

def main():
    data = sys.stdin.read().split()
    if not data:
        print("0 0")
        return

    n = int(data[0])
    m = int(data[1])
    edges = []
    index = 2
    graph = [[] for _ in range(n+1)]

    for i in range(m):
        u = int(data[index]); v = int(data[index+1]); index += 2
        edges.append((u, v))
        graph[u].append(v)
        graph[v].append(u)

    visited = [0] * (n+1)
    color = [-1] * (n+1)
    is_bipartite = True
    components = []
    comp_id = [-1] * (n+1)
    comp_count = 0

    for i in range(1, n+1):
        if visited[i] == 0:
            q = deque([i])
            visited[i] = 1
            color[i] = 0
            comp_nodes = [i]
            comp_id[i] = comp_count
            while q:
                node = q.popleft()
                for neighbor in graph[node]:
                    if visited[neighbor] == 0:
                        visited[neighbor] = 1
                        color[neighbor] = 1 - color[node]
                        comp_nodes.append(neighbor)
                        comp_id[neighbor] = comp_count
                        q.append(neighbor)
                    elif visited[neighbor] == 1:
                        if color[neighbor] == color[node]:
                            is_bipartite = False
            components.append(comp_nodes)
            comp_count += 1

    if is_bipartite:
        t = float('inf')
        total_ways = 0

        for comp_nodes in components:
            comp_graph = {}
            for node in comp_nodes:
                comp_graph[node] = set(graph[node])

            total_nodes = len(comp_nodes)
            part0 = [node for node in comp_nodes if color[node] == 0]
            part1 = [node for node in comp_nodes if color[node] == 1]

            ways_in_component = 0
            total_possible_edges_same_color = (len(part0)*(len(part0)-1))//2 + (len(part1)*(len(part1)-1))//2
            existing_edges_same_color = 0

            for node in part0:
                for neighbor in graph[node]:
                    if neighbor in part0:
                        existing_edges_same_color += 1
            for node in part1:
                for neighbor in graph[node]:
                    if neighbor in part1:
                        existing_edges_same_color += 1

            missing_edges_same_color = total_possible_edges_same_color - existing_edges_same_color
            ways_in_component = missing_edges_same_color

            total_ways += ways_in_component

            if ways_in_component > 0 and ways_in_component < t:
                t = 1

        if t == float('inf'):
            t = 3
            total_ways = 0
            for i in range(1, n+1):
                for j in range(i+1, n+1):
                    if i != j and (graph[i] and j not in graph[i]):
                        total_ways += 1
            if total_ways == 0:
                t = 0
                total_ways = 1
        else:
            t = 1
            if total_ways == 0:
                t = 0
                total_ways = 1

        print(f"{t} {total_ways}")
    else:
        print("0 1")

