    for j in range(1, K + 1):
        dp[1][j] = 1

    for i in range(2, N + 1):
        for j in range(1, K + 1):
            # For each possible previous max element less than j
            total = 0
            for prev_j in range(1, j):
                total += dp[i - 1][prev_j]
                if total >= MOD:
                    total -= MOD
            dp[i][j] = total

    # Sum all possibilities for the last set
    result = 0
    for j in range(1, K + 1):
        result += dp[N][j]
        if result >= MOD:
            result -= MOD

    print(result % MOD)

