        for char in seg:
            if node.children[char] is None:
                node.children[char] = TrieNode()
            node = node.children[char]
        node.is_end = True
    return root

def get_valid_segments():
    invalid = {"0011", "0101", "1110", "1111"}
    valid_segments = []
    for length in range(1, 5):
        for _ in range(2**length):
            seg = bin(_)[2:].zfill(length)
            if seg not in invalid:
                valid_segments.append(seg)
    return valid_segments

valid_segments_list = get_valid_segments()
root_trie = build_trie(valid_segments_list)

