def main():
    import sys
    data = sys.stdin.read().splitlines()
    m = int(data[0])
    chars = [line.strip() for line in data[1:1+m]]

    valid_tokens = {}
    for length in range(1, 5):
        total = 2 ** length
        if length == 4:
            forbidden = {"0011", "0101", "1110", "1111"}
            tokens = {"".join(bin(i)[2:].zfill(length)) for i in range(total)} - forbidden
        else:
            tokens = {"".join(bin(i)[2:].zfill(length)) for i in range(total)}
        for token in tokens:
            valid_tokens[token] = True

    states = {
        '': set(),
        '0': set(),
        '1': set(),
        '00': set(),
        '01': set(),
        '10': set(),
        '11': set(),
        '000': set(),
        '001': set(),
        '010': set(),
        '011': set(),
        '100': set(),
        '101': set(),
        '110': set(),
        '111': set()
    }

    for state_str in states:
        states[state_str] = set()

    dp = [dict() for _ in range(m)]
    all_words = set()
    ans = []
    current_state = ''
    for i, c in enumerate(chars):
        prev_state = current_state
        new_state_str = prev_state + c
        if len(new_state_str) > 3:
            new_state_str = new_state_str[-3:]
        current_state = new_state_str

        start_index = max(0, len(new_state_str) - 3)
        for j in range(start_index, len(new_state_str)):
            seg = new_state_str[j:]
            if len(seg) > 4:
                continue
            if valid_tokens.get(seg, False):
                seg_length = len(seg)
                remaining = new_state_str[:j]
                if j == 0:
                    word_set = {valid_tokens[seg]}
                else:
                    word_set = set()
                    for word_hash in states[remaining]:
                        new_word = word_hash + next(iter(valid_tokens))[0]
                        word_set.add(new_word)
                for word in word_set:
                    if word not in all_words:
                        all_words.add(word)
                states[new_state_str].update(word_set)
        ans.append(len(all_words))
    for a in ans:
        print(a)

