import math

def main():
    import sys
    data = sys.stdin.read().splitlines()
    n = int(data[0].strip())
    A = data[1].strip()
    B = data[2].strip()

    m = len(A)
    k = len(B)
    g = math.gcd(m, k)
    T = (m * k) // g

    mapping = {'R':0, 'S':1, 'P':2}

    def outcome(x, y):
        if x == y:
            return (0, 0)
        x_val = mapping[x]
        y_val = mapping[y]
        if (x_val + 1) % 3 == y_val:
            return (0, 1)
        else:
            return (1, 0)

    countA_dict = {}
    for r in range(g):
        for char in ['R', 'S', 'P']:
            countA_dict[(char, r)] = 0

    for idx, char in enumerate(A):
        r = idx % g
        countA_dict[(char, r)] += 1

    countB_dict = {}
    for r in range(g):
        for char in ['R', 'S', 'P']:
            countB_dict[(char, r)] = 0

    for idx, char in enumerate(B):
        r = idx % g
        countB_dict[(char, r)] += 1

    total_nike = 0
    total_poly = 0
    chars = ['R', 'S', 'P']

    for char1 in chars:
        for char2 in chars:
            freq = 0
            for r in range(g):
                key1 = (char1, r)
                key2 = (char2, r)
                if key1 in countA_dict and key2 in countB_dict:
                    freq += countA_dict[key1] * countB_dict[key2]
            res = outcome(char1, char2)
            total_nike += freq * res[0]
            total_poly += freq * res[1]

    full_periods = n // T
    remainder = n % T

    if remainder > 0:
        for i in range(remainder):
            idx_a = i % m
            idx_b = i % k
            a_char = A[idx_a]
            b_char = B[idx_b]
            res = outcome(a_char, b_char)
            total_nike += res[0]
            total_poly += res[1]

    print(total_nike, total_poly)

if __name__ == '__main__':
    main()

