    left_list = []
    for i, char in enumerate(l_str):
        left_list.append((char, i+1))

    right_list = []
    for i, char in enumerate(r_str):
        right_list.append((char, i+1))

    left_q = []
    right_q = []
    left_letters = {}
    right_letters = {}
    left_nonq = []
    right_nonq = []

    for char, idx in left_list:
        if char == '?':
            left_q.append(idx)
        else:
            left_nonq.append((char, idx))
            left_letters[char] = left_letters.get(char, 0) + 1

    for char, idx in right_list:
        if char == '?':
            right_q.append(idx)
        else:
            right_nonq.append((char, idx))
            right_letters[char] = right_letters.get(char, 0) + 1

    pairs = []

    # Phase 1: Match '?' with '?'
    q_count = min(len(left_q), len(right_q))
    for _ in range(q_count):
        l_idx = left_q.pop(0)
        r_idx = right_q.pop(0)
        pairs.append((l_idx, r_idx))

    # Phase 2: Match remaining '?' with letters
    left_remaining_q = left_q
    right_remaining_q = right_q

    for _ in range(len(left_remaining_q)):
        char_right = None
        for char, idx in right_nonq:
            if char == '*':
                continue
            if char == left_remaining_q[0]:
                char_right = (char, idx)
                break
        if char_right:
            pairs.append((left_remaining_q.pop(0), char_right[1]))
            idx_r = char_right[1]
            for i, (c, pos) in enumerate(right_nonq):
                if pos == idx_r:
                    del right_nonq[i]
                    break
        else:
            left_remaining_q.pop(0)

    for _ in range(len(right_remaining_q)):
        char_left = None
        for char, idx in left_nonq:
            if char == '*':
                continue
            if char == right_remaining_q[0]:
                char_left = (char, idx)
                break
        if char_left:
            pairs.append((char_left[1], right_remaining_q.pop(0)))
            idx_l = char_left[1]
            for i, (c, pos) in enumerate(left_nonq):
                if pos == idx_l:
                    del left_nonq[i]
                    break
        else:
            right_remaining_q.pop(0)

    # Phase 3: Match letters with same letters
    for char, cnt in left_letters.items():
        if char in right_letters:
            m = min(cnt, right_letters[char])
            for _ in range(m):
                pairs.append((left_nonq.pop(0)[1], right_nonq.pop(0)[1]))

    k = len(pairs)
    print(k)
    for a, b in pairs:
        print(f"{a} {b}")

