    used_left = set()
    used_right = set()

    # Match fixed left boots with fixed right boots
    for idx, color in l_fixed:
        if idx in used_left:
            continue
        if fixed_color_count.get(color, 0) > 0:
            fixed_color_count[color] -= 1
            ans_pairs.append((idx, r_fixed.pop()[0]))
            used_left.add(idx)
            used_right.add(r_fixed.pop()[0])

    # Match fixed right boots with fixed left boots (if any remaining)
    for idx, color in r_fixed:
        if idx in used_right:
            continue
        if fixed_color_count.get(color, 0) > 0:
            fixed_color_count[color] -= 1
            ans_pairs.append((l_fixed.pop()[0], idx))
            used_left.add(l_fixed.pop()[0])
            used_right.add(idx)

    # Match remaining wildcards
    remaining_wilds = l_wild + r_wild
    remaining_wilds.sort()
    num_remaining = len(remaining_wilds)
    pairs_from_wild = min(len(l_wild), len(r_wild))
