    extra_wilds = []
    if len(l_wild) > len(r_wild):
        extra_wilds = l_wild[len(r_wild):]
    elif len(r_wild) > len(l_wild):
        extra_wilds = r_wild[len(l_wild):]

    for ew in extra_wilds:
        ans_pairs.append((ew, 0))

