def bisect_left(a, x):
    lo, hi = 0, len(a)
    while lo < hi:
        mid = (lo + hi) // 2
        if a[mid][0] < x[0]:
            lo = mid + 1
        elif a[mid][0] > x[0]:
            hi = mid
        else:
            if a[mid][1] < x[1]:
                lo = mid + 1
            else:
                hi = mid
    return lo

