import sys
from collections import defaultdict

def build_A_struct(vouchers):
    if not vouchers:
        return None, None
    sorted_vouchers = sorted(vouchers, key=lambda x: x[0])
    arr_l = [v[0] for v in sorted_vouchers]
    arr_cost = [v[2] for v in sorted_vouchers]
    n = len(arr_cost)
    suffix_min = [0] * n
    suffix_min[-1] = arr_cost[-1]
    for i in range(n-2, -1, -1):
        suffix_min[i] = min(arr_cost[i], suffix_min[i+1])
    return arr_l, suffix_min

def build_B_struct(vouchers):
    if not vouchers:
        return None, None
    sorted_vouchers = sorted(vouchers, key=lambda x: x[1])
    arr_r = [v[1] for v in sorted_vouchers]
    arr_cost = [v[2] for v in sorted_vouchers]
    n = len(arr_cost)
    prefix_min = [0] * n
    prefix_min[0] = arr_cost[0]
    for i in range(1, n):
        prefix_min[i] = min(arr_cost[i], prefix_min[i-1])
    return arr_r, prefix_min

def query_A(arr_l, suffix_min, boundary):
    if arr_l is None or suffix_min is None:
        return None
    pos = bisect.bisect_left(arr_l, boundary)
    if pos >= len(arr_l):
        return None
    return suffix_min[pos]

def query_B(arr_r, prefix_min, boundary):
    if arr_r is None or prefix_min is None:
        return None
    pos = bisect.bisect_right(arr_r, boundary) - 1
    if pos < 0:
        return None
    return prefix_min[pos]

def process_same_group(d, vouchers, struct_A, struct_B, ans):
