def main():
    data = sys.stdin.read().split()
    if not data:
        print(-1)
        return

    n = int(data[0])
    x = int(data[1])
    vouchers = []
    index = 2
    for i in range(n):
        l = int(data[index])
        r = int(data[index+1])
        cost_val = int(data[index+2])
        index += 3
        vouchers.append((l, r, cost_val))

    duration_dict = {}
    for (l, r, cost_val) in vouchers:
        d = r - l + 1
        if d not in duration_dict:
            duration_dict[d] = []
        duration_dict[d].append((l, r, cost_val))

    structures = {}
    for d in duration_dict:
        lst = duration_dict[d]
        sorted_by_l = sorted(lst, key=lambda x: x[0])
        sorted_by_r = sorted(lst, key=lambda y: y[1])

        l_vals = [item[0] for item in sorted_by_l]
        r_vals = [item[1] for item in sorted_by_r]
        costs = [item[2] for item in lst]

        suffix_min = [0] * len(sorted_by_l)
        if sorted_by_l:
            min_cost_so_far = float('inf')
            for i in range(len(sorted_by_l)-1, -1, -1):
                if costs[i] < min_cost_so_far:
                    min_cost_so_far = costs[i]
                suffix_min[i] = min_cost_so_far

        prefix_min = [0] * len(sorted_by_r)
        if sorted_by_r:
            min_cost_so_far = float('inf')
            for i in range(len(sorted_by_r)):
                if costs[i] < min_cost_so_far:
                    min_cost_so_far = costs[i]
                prefix_min[i] = min_cost_so_far

        structures[d] = {
            'sorted_by_l': sorted_by_l,
            'suffix_min': suffix_min,
            'l_vals': l_vals,
            'sorted_by_r': sorted_by_r,
            'prefix_min': prefix_min,
            'r_vals': r_vals
        }

    best_total = float('inf')

    for (l, r, cost_i) in vouchers:
        d_i = r - l + 1
        d_t = x - d_i
        if d_t < 1 or d_t not in structures:
            continue

        cand1 = None
        cand2 = None

        s_by_l = structures[d_t]['sorted_by_l']
        l_vals_arr = structures[d_t]['l_vals']
        suffix_min_arr = structures[d_t]['suffix_min']
        pos1 = bisect_right(l_vals_arr, r)
        if pos1 < len(l_vals_arr):
            cand1 = suffix_min_arr[pos1]

        s_by_r = structures[d_t]['sorted_by_r']
        r_vals_arr = structures[d_t]['r_vals']
        prefix_min_arr = structures[d_t]['prefix_min']
        pos2 = bisect_left(r_vals_arr, l)
        if pos2 > 0:
            cand2 = prefix_min_arr[pos2-1]

        if cand1 is not None and cand2 is not None:
            total1 = cost_i + cand1
            total2 = cost_i + cand2
            candidate_total = min(total1, total2)
            if candidate_total < best_total:
                best_total = candidate_total
        elif cand1 is not None:
            if cost_i + cand1 < best_total:
                best_total = cost_i + cand1
        elif cand2 is not None:
            if cost_i + cand2 < best_total:
                best_total = cost_i + cand2

    if best_total == float('inf'):
        print(-1)
    else:
        print(best_total)

