def main():
    data = sys.stdin.read().split()
    if not data:
        print(0)
        return

    n = int(data[0])
    m = int(data[1])

    type1 = []
    type2 = []
    type3 = []

    index = 2
    for i in range(n):
        w = int(data[index]); c = int(data[index+1]); index += 2
        if w == 1:
            type1.append(c)
        elif w == 2:
            type2.append(c)
        elif w == 3:
            type3.append(c)

    type1.sort(reverse=True)
    type2.sort(reverse=True)
    type3.sort(reverse=True)

    def build_prefix(arr):
        arr.sort(reverse=True)
        pref = [0]
        total = 0
        for num in arr:
            total += num
            pref.append(total)
        return pref

    pref1 = build_prefix(type1)
    pref2 = build_prefix(type2)
    pref3 = build_prefix(type3)

    def get_max_cost(pref, max_items):
        if max_items < len(pref):
            return pref[max_items]
        else:
            return pref[-1]

    best = 0
    total_count3 = len(type3)
    for p in range(total_count3 + 1):
        weight_used3 = p * 3
        if weight_used3 > m:
            break
        remaining_weight = m - weight_used3
        if remaining_weight < 0:
            continue

        max_cost_here = 0

        max_count1 = remaining_weight
        max_count2 = remaining_weight // 2

        if max_count1 >= len(pref1):
            part1 = pref1[-1]
        else:
            part1 = pref1[max_count1]

        if max_count2 >= len(pref2):
            part2 = pref2[-1]
        else:
            part2 = pref2[max_count2]

        current = part1 + part2
        if current > max_cost_here:
            max_cost_here = current

        for k in range(1, len(pref2) + 1):
            weight_used2 = 2 * k
            if weight_used2 > remaining_weight:
                break
            remaining_after2 = remaining_weight - weight_used2
            part2_val = pref2[k]
            part1_val = get_max_cost(pref1, remaining_after2)
            total_val = part1_val + part2_val
            if total_val > max_cost_here:
                max_cost_here = total_val

        if max_cost_here > best:
            best = max_cost_here

    print(best)

