from collections import deque

def parse_constraints(program):
    lines = program.split('\n')
    constraints = []
    for line in lines:
        if '(' in line and ')' in line:
            parts = line.split(')')
            for part in parts:
                if '(' in part:
                    inner = part.split('(')
                    var_name = inner[0].strip()
                    if ',' in var_name:
                        var_indices = [int(x.strip()) for x in var_name.split(',')]
                    else:
                        var_indices = [int(var_name)]
                    constraint_str = inner[1].strip() if len(inner) > 1 else ''
                    constraints.append((var_indices, constraint_str))
    return constraints

def get_sequence_length(constraints):
    max_index = 0
    for indices, _ in constraints:
        for idx in indices:
            if idx > max_index:
                max_index = idx
    return max_index + 1

def backtrack(seq, constraints, index, current_vars, used):
    if index == len(seq):
        return seq

    for digit in range(10):
        new_seq = seq + [digit]
        valid = True
        for indices, op in constraints:
            if not check_constraint(new_seq, indices, op):
                valid = False
                break
        if valid:
            result = backtrack(new_seq, constraints, index + 1, current_vars, used)
            if result is not None:
                return result
    return None

def check_constraint(seq, indices, op_str):
    parts = op_str.split(',')
    if len(parts) != len(indices):
        return False
    left_val = seq[indices[0]]
    right_val = seq[indices[1]] if len(indices) >= 2 else None
    if right_val is None:
        return False
    if parts[0] == '>' and left_val > right_val:
        return True
    elif parts[0] == '<' and left_val < right_val:
        return True
    elif parts[0] == '=' and left_val == right_val:
        return True
    elif parts[0] == '>=' and left_val >= right_val:
        return True
    elif parts[0] == '<=' and left_val <= right_val:
        return True
    return False

def solve_golorp(program):
    constraints = parse_constraints(program)
    n = get_sequence_length(constraints)
    seq = []
    result = backtrack(seq, constraints, 0, [], [])
    if result is None:
        return "false"
    return ''.join(map(str, result))

