import sys

def main():
    data = sys.stdin.read().splitlines()
    if not data:
        return

    results = []
    for line in data:
        s = line.strip()
        if not s:
            continue

        n = s.count('?')
        if n == 0:
            results.append("false")
            continue

        total_chars = len(s)
        max_cols = 1
        while max_cols * max_cols < total_chars:
            max_cols += 1
        cols = max_cols
        rows = (total_chars + cols - 1) // cols

        grid = [[' ' for _ in range(cols)] for _ in range(rows)]
        index = 0
        for i in range(rows):
            for j in range(cols):
                if index < total_chars:
                    grid[i][j] = s[index]
                    index += 1

        stack = []
        ip_row, ip_col = 0, 0
        direction = (0, 1)
        visited = set()
        found_sequence = None

        def simulate(sequence):
            nonlocal stack, ip_row, ip_col, direction, visited
            stack = []
            ip_row, ip_col = 0, 0
            direction = (0, 1)
            visited = set()
            for idx, char in enumerate(sequence):
                if ip_row < 0 or ip_row >= rows or ip_col < 0 or ip_col >= cols:
                    return False
                if (ip_row, ip_col) in visited:
                    return False
                visited.add((ip_row, ip_col))
                cell = grid[ip_row][ip_col]
                if cell == ' ': 
                    return False
                if cell.isdigit() or cell == '?':
                    if cell == '?':
                        val = sequence[idx]
                    else:
                        val = int(cell)
                    stack.append(val)
                elif cell == '~':
                    stack.append(-1)
                elif cell == '+':
                    if len(stack) < 2:
                        return False
                    a = stack.pop()
                    b = stack.pop()
                    stack.append(a + b)
                elif cell == '-':
                    if len(stack) < 2:
                        return False
                    a = stack.pop()
                    b = stack.pop()
                    stack.append(b - a)
                elif cell == '*':
                    if len(stack) < 2:
                        return False
                    a = stack.pop()
                    b = stack.pop()
                    stack.append(a * b)
                elif cell == '/':
                    if len(stack) < 2:
                        return False
                    a = stack.pop()
                    b = stack.pop()
                    if a == 0:
                        return False
                    stack.append(b // a)
                elif cell == '%':
                    if len(stack) < 2:
                        return False
                    a = stack.pop()
                    b = stack.pop()
                    if a == 0:
                        return False
                    stack.append(b % a)
                elif cell == '>':
                    direction = (0, 1)
                elif cell == '<':
                    direction = (0, -1)
                elif cell == '^':
                    direction = (-1, 0)
                elif cell == 'v':
                    direction = (1, 0)
                elif cell == '_':
                    direction = (0, -1 if stack and stack[-1] != 0 else 1)
                elif cell == '$':
                    if stack:
                        stack.pop()
                elif cell == '!':
                    if stack:
                        a = stack.pop()
                        if a != 0 and len(stack) >= 2:
                            stack.append(stack.pop())
                elif cell == ':':
                    if stack:
                        stack.append(stack[-1])
                elif cell == ' ':
                    continue
                next_row = ip_row + direction[0]
                next_col = ip_col + direction[1]
                if next_row < 0 or next_row >= rows or next_col < 0 or next_col >= cols:
                    if cell == '@':
                        return True
                    else:
                        return False
                ip_row, ip_col = next_row, next_col
            return False

        if n == 0:
            results.append("false")
        else:
            sequence_found = None
            for seq in product('0123456789', repeat=n):
                seq_str = ''.join(seq)
                if simulate(seq_str):
                    sequence_found = seq_str
                    break
            if sequence_found is None:
                results.append("false")
            else:
                results.append(sequence_found)

    for res in results:
        print(res)

