def parse_golorp_name(name):
    # Extract the pattern from the name
    pattern = re.search(r'$$.*?$$', name).group(0)[1:-1]

    # Parse the input and output conditions
    input_conditions = []
    output_conditions = []

    # Split the pattern into parts
    parts = pattern.split(':')
    input_part = parts[0]
    output_part = parts[1] if len(parts) > 1 else ''

    # Parse input conditions: e.g., 'a>0' -> (a, '>', 0)
    input_conditions = []
    for cond in input_part.split(','):
        if not cond.strip():
            continue
        a, op, b = cond.split('>')
        input_conditions.append((a, op, int(b)))

    # Parse output conditions: e.g., 'a<0' -> (a, '<', 0)
    output_conditions = []
    for cond in output_part.split(','):
        if not cond.strip():
            continue
        a, op, b = cond.split('<')
        output_conditions.append((a, op, int(b)))

    return input_conditions, output_conditions

def is_valid_sequence(sequence, input_conditions, output_conditions):
    # Map variable names to indices in the sequence
    var_to_index = {var: i for i, var in enumerate(set([cond[0] for cond in input_conditions + output_conditions]))}

    # Convert sequence to a dictionary of variable values
    var_values = {var: int(seq[var_to_index[var]]) for var in var_to_index}

    # Check input conditions
    for var, op, val in input_conditions:
        if op == '>':
            if var_values[var] <= val:
                return False
        elif op == '<':
            if var_values[var] >= val:
                return False
        elif op == '=':
            if var_values[var] != val:
                return False

    # Check output conditions
    for var, op, val in output_conditions:
        if op == '>':
            if var_values[var] <= val:
                return False
        elif op == '<':
            if var_values[var] >= val:
                return False
        elif op == '=':
            if var_values[var] != val:
                return False

    return True

def find_lex_smallest_sequence(input_conditions, output_conditions):
    # Get all variables involved
    variables = set([cond[0] for cond in input_conditions + output_conditions])
    num_vars = len(variables)

    # Try all combinations of digits from 0 to 9 for each variable
    from itertools import product

    for seq in product(range(10), repeat=num_vars):
        # Map sequence to variable names
        var_to_index = {var: i for i, var in enumerate(sorted(variables))}
        seq_str = ''.join(str(seq[var_to_index[var]]) for var in sorted(variables))

        if is_valid_sequence(seq_str, input_conditions, output_conditions):
            return seq_str

    return "false"

