import re

def parse_golorp_name(name):
    # Extract the expression part inside the parentheses
    match = re.search(r'\(([^)]+)\)', name)
    if not match:
        return None, None

    expr = match.group(1)

    # Extract the conditions
    conditions = []
    for condition in re.split(r'[:-]', expr):
        if condition.strip():
            conditions.append(condition.strip())

    return expr, conditions

def is_valid_condition(condition):
    # Check if the condition is valid (contains only digits and operators)
    return all(c.isdigit() or c in '+-*/()' for c in condition)

def parse_conditions(conditions):
    # Parse each condition into a tuple of (left, op, right)
    parsed = []
    for cond in conditions:
        parts = re.split(r'([+\\-*/])', cond)
        left = parts[0]
        op = parts[1]
        right = parts[2]
        parsed.append((left, op, right))
    return parsed

def evaluate_condition(left, op, right, values):
    # Evaluate the condition with given values
    left_val = int(values[int(left) - 1]) if left.isdigit() else int(values[int(left[1:]) - 1])
    right_val = int(values[int(right) - 1]) if right.isdigit() else int(values[int(right[1:]) - 1])

    if op == '+':
        return left_val + right_val
    elif op == '-':
        return left_val - right_val
    elif op == '*':
        return left_val * right_val
    elif op == '/':
        return left_val // right_val
    else:
        raise ValueError(f"Unknown operator: {op}")

def is_valid_sequence(values, conditions):
    for left, op, right in conditions:
        try:
            result = evaluate_condition(left, op, right, values)
            if op == '>':
                if result <= 0:
                    return False
            elif op == '<':
                if result >= 0:
                    return False
        except:
            return False
    return True

def find_lex_smallest_sequence(length, conditions):
    # Try to find the lexicographically smallest sequence
    from itertools import product

    for seq in product(range(10), repeat=length):
        if is_valid_sequence(seq, conditions):
            return ''.join(map(str, seq))
    return None

def main():
    input_str = sys.stdin.read().strip()
    expr, conditions = parse_golorp_name(input_str)

    if not expr or not conditions:
        print("false")
        return

    if not all(is_valid_condition(cond) for cond in conditions):
