    total_teams = 2 ** n
    num_phases = n

    s_list = list(s_str)

    def solve(start, end, phase_index, current_champion, used_skills, memo):
        if phase_index == num_phases:
            return True

        key = (start, end, phase_index, tuple(sorted(used_skills)))
        if key in memo:
            return memo[key]

        mid = (start + end) // 2
        left_start = start
        left_end = mid - 1
        right_start = mid + 1
        right_end = end

        left_available = list(range(left_start, left_end + 1))
        right_available = list(range(right_start, right_end + 1))

        rule_char = s_list[phase_index]
        rule = 0 if rule_char == '0' else 1

        found_left = False
        found_right = False

        for i in range(left_available):
            if i not in used_skills:
                for j in range(right_available):
                    if j not in used_skills:
                        candidate_left = i
                        candidate_right = j
                        new_used = set(used_skills)
                        new_used.add(candidate_left)
                        new_used.add(candidate_right)

                        if rule == 1:
                            if candidate_left > candidate_right:
                                found_left = True
                                break
                        else:
                            if candidate_left < candidate_right:
                                found_left = True
                                break

                        if rule == 1:
                            if candidate_right > candidate_left:
                                found_right = True
                                break
                        else:
                            if candidate_right < candidate_left:
                                found_right = True
                                break

                if found_left or found_right:
                    break

        result = False
        if not found_left:
            left_winner = None
            for candidate in range(left_available):
                if candidate not in used_skills:
                    left_winner = candidate
                    break
            if left_winner is not None:
                new_used.add(left_winner)
                if rule == 1:
                    if current_champion > left_winner:
                        result = True
                    else:
                        result = (current_champion == left_winner)
                else:
                    if current_champion < left_winner:
                        result = True
                    else:
                        result = (current_champion == left_winner)
        elif not found_right:
            right_winner = None
            for candidate in range(right_available):
                if candidate not in used_skills:
                    right_winner = candidate
                    break
            if right_winner is not None:
                new_used.add(right_winner)
                if rule == 1:
                    if current_champion > right_winner:
                        result = True
                    else:
                        result = (current_champion == right_winner)
                else:
                    if current_champion < right_winner:
                        result = True
                    else:
                        result = (current_champion == right_winner)
        else:
            for candidate_left in range(left_available):
                if candidate_left not in used_skills:
                    for candidate_right in range(right_available):
                        if candidate_right not in used_skills:
                            new_used = set(used_skills)
                            new_used.add(candidate_left)
                            new_used.add(candidate_right)

                            if rule == 1:
                                if candidate_left > candidate_right:
                                    winner = candidate_left
                                    new_used.add(winner)
                                    if winner == current_champion:
                                        result = solve(mid + 1, end, phase_index + 1, winner, new_used, memo)
                                    else:
                                        result = solve(mid + 1, end, phase_index + 1, current_champion, new_used, memo)
                                    break
                            else:
                                if candidate_left < candidate_right:
                                    winner = candidate_left
                                    new_used.add(winner)
                                    if winner == current_champion:
                                        result = solve(mid + 1, end, phase_index + 1, winner, new_used, memo)
                                    else:
                                        result = solve(mid + 1, end, phase_index + 1, current_champion, new_used, memo)
                                    break

                            if rule == 1:
                                if candidate_right > candidate_left:
                                    winner = candidate_right
                                    new_used.add(winner)
                                    if winner == current_champion:
                                        result = solve(mid + 1, end, phase_index + 1, winner, new_used, memo)
                                    else:
